import{_ as e,c as a,o as t,a as p}from"./app.55c76ae1.js";const g=JSON.parse('{"title":"\u5047\u671F\u56DE\u987E\u3001\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5047\u671F\u56DE\u987E","slug":"\u5047\u671F\u56DE\u987E"},{"level":2,"title":"\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6","slug":"\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6"},{"level":2,"title":"\u4ECA\u65E5\u5206\u4EAB","slug":"\u4ECA\u65E5\u5206\u4EAB"}],"relativePath":"blog/220508.md"}'),r={name:"blog/220508.md"},i=p('<h1 id="\u5047\u671F\u56DE\u987E\u3001\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6" tabindex="-1">\u5047\u671F\u56DE\u987E\u3001\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6 <a class="header-anchor" href="#\u5047\u671F\u56DE\u987E\u3001\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6" aria-hidden="true">#</a></h1><h2 id="\u5047\u671F\u56DE\u987E" tabindex="-1">\u5047\u671F\u56DE\u987E <a class="header-anchor" href="#\u5047\u671F\u56DE\u987E" aria-hidden="true">#</a></h2><p>\u4E94\u4E00\u5047\u671F\uFF0C\u52A0\u4E0A\u4E94\u56DB\u6821\u5E86\uFF0C\u8FDE\u653E 9 \u5929\u5047\u3002\u5317\u4EAC\u6B63\u5904\u75AB\u60C5\u4E4B\u4E0B\uFF0C9 \u5929\u5047\u671F\uFF0C\u505A\u4E86 7 \u6B21\u6838\u9178\uFF0C\u51FA\u4E86 0 \u6B21\u6821\u56ED\u3002\u5982\u4ECA\u5047\u671F\u7ED3\u675F\uFF0C\u660E\u5929\u5F00\u59CB\u4E0A\u7F51\u8BFE\uFF0C\u6765\u56DE\u987E\u4E00\u4E0B\u8FC7\u53BB 9 \u5929\u505A\u4E86\u4EC0\u4E48\uFF1A</p><ul><li><strong>\u5A31\u4E50\uFF1A</strong> \u770B\u4E86\u51E0\u90E8\u6770\u68EE \xB7 \u65AF\u5766\u68EE\u4E3B\u6F14\u7684\u7535\u5F71\uFF0C\u4E2D\u89C4\u4E2D\u77E9\u7684\u52A8\u4F5C\u7247\u3002\u6253\u6E38\u620F\uFF0C\u6700\u8FD1\u559C\u6B22\u5355\u6392\u3002</li><li><strong>\u4F53\u80B2\uFF1A</strong> \u8FD9\u5B66\u671F\u9009\u4FEE\u7684\u4E52\u4E53\u7403\uFF0C\u6B63\u624B\u653B\u7403\u8FDB\u6B65\u633A\u5927(\u4E2A\u4EBA\u89C9\u5F97)\u3002</li><li><strong>\u9605\u8BFB\uFF1A</strong> \u4E70\u4E86\u5317\u5C9B\u6700\u65B0\u7684\u4E66\u300A\u5FC5\u6709\u4EBA\u91CD\u5199\u7231\u60C5\u300B\uFF0C\u5176\u4E2D\u4E0D\u5C11\u8BD7\u6B4C\u6563\u6587\u662F\u4EE5\u524D\u5C31\u8BFB\u8FC7\u7684\u3002\u5F88\u559C\u6B22\u5317\u5C9B\u7684\u6587\u98CE\uFF0C\u653E\u5728\u5E8A\u5934\u7761\u524D\u8BFB\u4E00\u8BFB\u3002</li><li><strong>\u5B66\u4E60\uFF1A</strong> \u5F00\u59CB\u7CFB\u7EDF\u5B66\u4E60\u8BA1\u7B97\u673A\u4E13\u4E1A\u7684\u8BFE\u7A0B\u3002</li></ul><p>\u8FD9\u91CC\u9700\u8981\u8BF4\u660E\uFF0C\u6211\u662F\u4E00\u540D\u533B\u5B66\u751F\uFF0C\u51C6\u5907\u8003\u672C\u6821\u8F6F\u4EF6\u4E0E\u5FAE\u7535\u5B50\u5B66\u9662\u7684\u7814\u7A76\u751F\uFF0C\u6240\u4EE5\u5F00\u59CB\u81EA\u5B66\u76F8\u5173\u8BFE\u7A0B\u3002\u76EE\u524D\u5728\u5B66 C \u8BED\u8A00\uFF0C\u6211\u6700\u719F\u7EC3\u7684\u7F16\u7A0B\u8BED\u8A00\u662F Python\uFF0C\u4F46\u4E4B\u524D\u5B66\u5F97\u6BD4\u8F83\u6742\uFF0C\u51C6\u5907\u6DF1\u5165\u5B66\u4E60\u673A\u5668\u5B66\u4E60\u76F8\u5173\u77E5\u8BC6\u3002\u4F60\u53EF\u4EE5\u5728\u672C\u7AD9\u4E0A\u770B\u5230\u6211\u6709\u5173\u7684\u5B66\u4E60\u7B14\u8BB0\u3002</p><h2 id="\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6" tabindex="-1">\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6 <a class="header-anchor" href="#\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6" aria-hidden="true">#</a></h2><p>\u57C3\u91CC\u514B\xB7\u83B1\u65AF(Eric Ries)\u5728\u300A\u7CBE\u76CA\u521B\u4E1A\u300B(The Lean Startup)\u4E2D\u63D0\u51FA\u300C\u6700\u5C0F\u53EF\u884C\u4EA7\u54C1\u300D(Minimum Viable Product\uFF0CMVP)\u7684\u6982\u5FF5\u3002\u56F4\u7ED5\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u521B\u4E1A\u8005\u5728\u521B\u4E1A\u521D\u671F\u4E0D\u53EF\u8D2A\u591A\u6C42\u5168\uFF0C\u800C\u5E94\u8BE5\u5148\u505A\u51FA\u4E00\u4E2A\u6700\u5C0F\u7684\u53EF\u7528\u4EA7\u54C1\uFF0C\u62FF\u5230\u5E02\u573A\u4E0A\u53BB\u68C0\u9A8C\uFF0C\u7136\u540E\u6839\u636E\u53CD\u9988\u53CD\u590D\u8FED\u4EE3\uFF0C\u6253\u78E8\u5347\u7EA7\uFF0C\u6700\u7EC8\u505A\u51FA\u6BD4\u8F83\u5B8C\u5584\u3001\u6BD4\u8F83\u6210\u529F\u7684\u4EA7\u54C1\u3002</p><p>\u7531\u6B64\u5F15\u7533\u51FA\u300C\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6\u300D(Minimal Actionable Knowledge and Experience\uFF0CMAKE)\u3002MAKE \u6307\u7684\u662F\u5165\u95E8\u67D0\u4E2A\u65B0\u9886\u57DF\u5207\u5B9E\u53EF\u884C\u7684\u6700\u5C0F\u77E5\u8BC6\u96C6\u5408\u3002\u8BF4\u8D77\u6765\u597D\u50CF\u6BD4\u8F83\u9AD8\u7EA7\uFF0C\u4F46\u5B9E\u9645\u4E0A\uFF0CMAKE \u80CC\u540E\u6709\u4E00\u4E2A\u652F\u6491\u5B83\u7684\u6734\u7D20\u539F\u5219 \u2014 Pareto \u539F\u5219(\u4EA6\u79F0 80-20 \u539F\u5219)\uFF0C\u5373 80% \u7684\u5DE5\u4F5C\u95EE\u9898\u53EF\u4EE5\u901A\u8FC7\u638C\u63E1 20% \u7684\u77E5\u8BC6\u6765\u89E3\u51B3\u3002</p><p>\u5728\u5B66\u4E60\u67D0\u9879\u6280\u80FD\u65F6\uFF0C\u6211\u4EEC\u8981\u60F3\u529E\u6CD5\u5728\u6700\u77ED\u7684\u65F6\u95F4\u5185\uFF0C\u6478\u7D22\u6E05\u695A\u8FD9\u9879\u6280\u80FD\u7684\u300C\u6700\u5C11\u5FC5\u8981\u77E5\u8BC6\u300D\u3002\u4E00\u65B9\u9762\uFF0C\u5B83\u5DF2\u7136\u53EF\u4EE5\u5E2E\u6211\u4EEC\u89E3\u51B3\u5DE5\u4F5C\u4E2D\u7684\u5927\u90E8\u5206\u95EE\u9898\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u5165\u95E8\u4E4B\u540E\uFF0C\u6280\u80FD\u7684\u63D0\u5347\u901A\u9053\u53EF\u4EE5\u5728\u5B9E\u8DF5\u4E2D\u5BFB\u5F97\uFF0C\u7F3A\u5565\u8865\u5565\u3002\u6709\u660E\u786E\u7684\u4EFB\u52A1\u5BFC\u5411\uFF0C\u5B66\u4E60\u5C31\u4F1A\u6709\u5982\u795E\u52A9\uFF0C\u8FD9\u4E5F\u662F\u5F53\u524D\u65F6\u4EE3\u7684\u5FEB\u8282\u594F\u5B66\u4E60\u6CD5\u3002</p><h2 id="\u4ECA\u65E5\u5206\u4EAB" tabindex="-1">\u4ECA\u65E5\u5206\u4EAB <a class="header-anchor" href="#\u4ECA\u65E5\u5206\u4EAB" aria-hidden="true">#</a></h2><p>\u524D\u9762\u63D0\u5230\u6211\u5F88\u559C\u6B22\u5317\u5C9B\u7684\u6587\u98CE\uFF0C\u4ECA\u65E5\u5206\u4EAB\u4ED6\u7684\u4E00\u9996\u8BD7\uFF1A</p><div class="tip custom-block"><p class="custom-block-title"><strong>\u65E0\u9898</strong></p><p>\u5BF9\u4E8E\u4E16\u754C</p><p>\u6211\u6C38\u8FDC\u662F\u964C\u751F\u4EBA</p><p>\u6211\u4E0D\u61C2\u5B83\u7684\u8BED\u8A00</p><p>\u5B83\u4E0D\u61C2\u6211\u7684\u6C89\u9ED8</p><p>\u6211\u4EEC\u4EA4\u6362\u7684</p><p>\u53EA\u662F\u4E00\u70B9\u8F7B\u8511</p><p>\u5982\u540C\u76F8\u9022\u5728\u955C\u5B50\u4E2D</p><p>\u5BF9\u4E8E\u81EA\u5DF1</p><p>\u6211\u6C38\u8FDC\u662F\u4E2A\u964C\u751F\u4EBA</p><p>\u6211\u754F\u60E7\u9ED1\u6697</p><p>\u5374\u7528\u8EAB\u4F53\u6321\u4F4F\u4E86</p><p>\u90A3\u76CF\u552F\u4E00\u7684\u706F</p><p>\u6211\u7684\u5F71\u5B50\u662F\u6211\u7684\u60C5\u4EBA</p><p>\u5FC3\u662F\u4EC7\u654C</p></div><p>\u559C\u6B22\u5B64\u72EC\u7684\u65F6\u5019\u5728\u5FC3\u91CC\u6162\u6162\u56DE\u5473\u8FD9\u9996\u8BD7\u2026\u2026</p><p>2022.05.08</p>',14),s=[i];function n(o,l,d,c,h,_){return t(),a("div",null,s)}var m=e(r,[["render",n]]);export{g as __pageData,m as default};
